import{_ as a,c as s,o as n,e as l}from"./app.b24d80f6.js";const A=JSON.parse('{"title":"Data URL","description":"","frontmatter":{},"headers":[{"level":2,"title":"Data URL 的优缺点","slug":"data-url-的优缺点","link":"#data-url-的优缺点","children":[]}],"relativePath":"files/docs/JavaScript/12-Data URL.md","lastUpdated":1680151218000}'),t={name:"files/docs/JavaScript/12-Data URL.md"},e=l(`<h1 id="data-url" tabindex="-1"><a href="https://developer.mozilla.org/zh-CN/docs/Web/HTTP/Basics_of_HTTP/Data_URLs" target="_blank" rel="noreferrer">Data URL</a> <a class="header-anchor" href="#data-url" aria-hidden="true">#</a></h1><blockquote><p>Data URL，即前缀为 <strong>data:</strong> 协议的 URL，其允许内容创建者向文档中嵌入小文件。</p></blockquote><p><strong>Data URL</strong> 由四个部分组成：前缀（data:）、指示数据类型的 MIME 类型、如果非文本则为可选的 <strong>base64</strong> 标记、数据本身</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">data:[&lt;mediatype&gt;</span><span style="color:#A6ACCD;">]</span><span style="color:#89DDFF;">[;</span><span style="color:#A6ACCD;">base64</span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;">,</span><span style="color:#89DDFF;">&lt;</span><span style="color:#A6ACCD;">data</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#FFCB6B;">data:[&lt;mime</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">typ</span><span style="color:#A6ACCD;">e</span><span style="color:#89DDFF;">&gt;</span><span style="color:#C3E88D;">][</span><span style="color:#89DDFF;">;</span><span style="color:#A6ACCD;">charset</span><span style="color:#89DDFF;">=&lt;</span><span style="color:#C3E88D;">charse</span><span style="color:#A6ACCD;">t</span><span style="color:#89DDFF;">&gt;</span><span style="color:#C3E88D;">][</span><span style="color:#89DDFF;">;&lt;</span><span style="color:#A6ACCD;">encoding</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">],</span><span style="color:#89DDFF;">&lt;</span><span style="color:#A6ACCD;">encoded data</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span></code></pre></div><p>目前支持的 <code>&lt;mime type&gt;</code> 有以下格式</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#A6ACCD;">data:,</span></span>
<span class="line"><span style="color:#A6ACCD;">data:text/plain,</span></span>
<span class="line"><span style="color:#A6ACCD;">data:text/html,</span></span>
<span class="line"><span style="color:#A6ACCD;">data:text/html;base64,</span></span>
<span class="line"><span style="color:#A6ACCD;">data:text/css,</span></span>
<span class="line"><span style="color:#A6ACCD;">data:text/css;base64,</span></span>
<span class="line"><span style="color:#A6ACCD;">data:text/javascript,</span></span>
<span class="line"><span style="color:#A6ACCD;">data:text/javascript;base64,</span></span>
<span class="line"><span style="color:#A6ACCD;">data:image/gif;base64,</span></span>
<span class="line"><span style="color:#A6ACCD;">data:image/png;base64,</span></span>
<span class="line"><span style="color:#A6ACCD;">data:image/jpeg;base64,</span></span>
<span class="line"><span style="color:#A6ACCD;">data:image/x-icon;base64</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><p>使用时候,直接在<code>&lt;img&gt;</code> src 属性，或者在css中 url 直接使用</p><div class="language-html"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">img</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">src</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">data:xxxxxxxxxxxx</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;"> /&gt;</span></span>
<span class="line"></span></code></pre></div><h2 id="data-url-的优缺点" tabindex="-1">Data URL 的优缺点 <a class="header-anchor" href="#data-url-的优缺点" aria-hidden="true">#</a></h2><p>优点</p><ul><li>当访问外部资源很麻烦或受限时</li><li>当图片是在服务器端用程序动态生成，每个访问用户显示的都不同时。</li><li>可以减少网络请求（当图片的体积太小，占用一个HTTP会话不是很值得时）。</li><li>字符串编码方便传输存储。</li></ul><p>缺点</p><ul><li>Base64编码的数据体积通常是原数据的体积4/3，也就是Data URL形式的图片会比二进制格式的图片体积大1/3。</li><li>Data URL形式的图片不会被浏览器缓存，这意味着每次访问这样页面时都被下载一次。这是一个使用效率方面的问题——尤其当这个图片被整个网站大量使用的时候。</li><li>不能在客户端进行缓存。（如图片，只能通过css文件进行背景图片缓存）</li><li>渲染时需要base64解码，需要消耗cpu资源。</li></ul>`,13),p=[e];function o(c,r,i,d,D,C){return n(),s("div",null,p)}const g=a(t,[["render",o]]);export{A as __pageData,g as default};
