import{_ as s,c as a,o as e,af as i}from"./chunks/framework.Cj92BNWl.js";const k=JSON.parse('{"title":"https 中获取 http 资源问题","description":"","frontmatter":{},"headers":[],"relativePath":"files/wrong/https获取http资源.md","filePath":"files/wrong/https获取http资源.md","lastUpdated":1738744669000}'),n={name:"files/wrong/https获取http资源.md"};function p(r,t,h,o,l,g){return e(),a("div",null,t[0]||(t[0]=[i('<h1 id="https-中获取-http-资源问题" tabindex="-1">https 中获取 http 资源问题 <a class="header-anchor" href="#https-中获取-http-资源问题" aria-label="Permalink to &quot;https 中获取 http 资源问题&quot;">​</a></h1><h2 id="原因" tabindex="-1">原因 <a class="header-anchor" href="#原因" aria-label="Permalink to &quot;原因&quot;">​</a></h2><p>由于谷歌在一次更新中加入了对于 <strong>混合内容</strong> 的处理</p><p>在浏览器中访问 <strong>https</strong> 页面时，如果该 <strong>htpps</strong> 页面中有一些 <strong>http</strong> 资源，我们可以把这些 <strong>http</strong> 资源，叫做<strong>混合内容</strong>（<strong>Mixed Content</strong>）。<strong>htpps</strong> 页面在显示<strong>混合内容</strong>时候，会出现以下问题：</p><ol><li><p>加载了混合内容，但会出现警告</p></li><li><p>不加载混合内容，直接会显示空白内容</p></li><li><p>在加载混合内容之前，会出现类似是否“显示”，或存在不安全风险而被“阻止”的提示！</p></li></ol><h2 id="解决办法" tabindex="-1">解决办法 <a class="header-anchor" href="#解决办法" aria-label="Permalink to &quot;解决办法&quot;">​</a></h2><p>其中一种解决办法是在html头部中添加如下信息</p><p>浏览器在解析请求的时候会自动把 <strong>http</strong> 请求转化为 <strong>https</strong> 请求。（ps: <strong>https</strong>可正常访问）</p><div class="language-html vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">&lt;</span><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">meta</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;"> http-equiv</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">=</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;Content-Security-Policy&quot;</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;"> content</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">=</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;upgrade-insecure-requests&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">&gt;</span></span></code></pre></div><hr><p><a href="https://blog.chromium.org/2019/10/no-more-mixed-messages-about-https.html" target="_blank" rel="noreferrer">No More Mixed Messages About HTTPS</a></p>',11)]))}const c=s(n,[["render",p]]);export{k as __pageData,c as default};
